import React from "react";
import toast from "react-hot-toast";
import { useDispatch, useSelector } from "react-redux";
import {add ,remove} from "../redux/Slices/CartSlice";
 const Product = ({post}) =>{ 

     const {cart}=useSelector((state) => state);
     const dispatch =useDispatch();

     const addtoCart =()=>{
          dispatch(add(post));
          toast.success("Item added to Cart");
     } 

     const removeFromCart =()=>{
          dispatch(remove(post.id));
          toast.error("Item removed from Cart");
     } 
    return(
        <div className="flex flex-col justify-center items-center
                         hover:scale-110  transition duration-300 ease-in gap-3 mt-10 ml-5 p-4 rounded-lg outline
        " >
           <div className="text-gray-700 font-semibold text-lg text-left truncate w-40 mt-1">
                <p>{post.title} </p>
           </div>
           <div className="w-40 text-gray-400 font-normal text-[10px] text-left">
                <p>{post.description.split(" ").slice(0,10).join(" ") + "..." }</p>
           </div>
           <div className="h-25">
                <img src={post.image}  className="h-full w-full"/>
           </div>
           <div className="flex flex-row justify-between gap-12">
               <div>
                    <p
                         className="text-green-600 font-semibold"
                    >${post.price}</p>
               </div>  
               {
              cart.some( (p) => p.id == post.id) ? 
              (<button
              onClick={removeFromCart}
              >
                    Remove Item
              </button>) :
              (<button
               onClick={addtoCart} 
              >
                    Add to Cart
              </button>) 
               }
          </div>
           
        </div>
    )
};

export default Product;
