import React from "react";
import { useSelector } from "react-redux";


 const Cart = () =>{ 

    const{cart} = useSelector( (state) => state);

    return(
        <div>
        {
           cart.length > 0 ? 
           (<div></div>) 
           :
            (<div>
                <h1>Your Cart is empty</h1>
                <Link to={"/"}>
                    <button>
                        Shop Now
                    </button>
                </Link>
            </div>)
        }
        </div>
    )
};

export default Cart;
